const newValue = Object.values(items[0].json.documents).filter(it => it.category === 'article').filter(it => it.children.length === 0).map(it => ({
  json: {
      url: it.url,
  title: it.title,
  author: it.author,
  summary: it.summary,
  saved_at: new Date(it.saved_at),
  }
}))


return newValue;
