function extractPlaceholders(str) {
    // Regular expression to match placeholders
    // It matches any alphanumeric character including dashes and underscores between {}
    const regex = /\{([a-zA-Z0-9_-]+)\}/g;

    // Set to store unique placeholders
    const uniquePlaceholders = new Set();

    // Extract and store unique placeholders
    let match;
    while ((match = regex.exec(str)) !== null) {
        uniquePlaceholders.add(match[1]);
    }

    // Convert the Set to an array and return
    return Array.from(uniquePlaceholders);
}

