// Because emails are sent slightly after the schedule trigger runs, we'll end up waiting an extra day to send unless we take into account the execution time of the workflow
let buffer_mins = 20

let templates = $('Email sequence').first().json.emails

function next_sequence_number(messages, mail_id, sender_name) {
  for (let i = 0; i < messages.length; i++) {
    if(!('html' in messages[i])) return -1;
    let in_campaign = messages[i].html.includes("data-cam='"+mail_id+"'")
    let valid_seq = messages[i].html.includes("data-seq='"+i+"'")
    let from_us = messages[i].From.includes(sender_name)
    console.log(in_campaign + ", " + valid_seq + ", " + from_us)
    if(!(from_us && in_campaign && valid_seq)) {
      return -1;
    }
  }
  return messages.length;
}


