let missingCredentials = [];
for (const credential of $('Remove Duplicate Credentials by Name').all()) {
  let type = credential.json.type;
  let oldName = credential.json.name;
  let name = $('Map Credentials').first().json[credential.json.name];
  if (name != "[create new]") {
    for (const credentialData of $('Merge').first().json.allCredentials) {
      if (credentialData.name == name) {
        id = credentialData.id;
        continue;
      }
    }
    missingCredentials.push({
      "oldName": oldName,
      "name": name,
      "type": type,
      "id": id
    });
  }
}

return missingCredentials;

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
let workflowData = $('Merge').first().json.workflowData;
for (const credential of $input.all()) {  
  for (const nodes of workflowData.nodes) {
    if (nodes.credentials 
        && nodes.credentials[credential.json.type] !== undefined 
        && nodes.credentials[credential.json.type].name == credential.json.oldName) {
      nodes.credentials[credential.json.type].id = credential.json.id;
      nodes.credentials[credential.json.type].name = credential.json.name;
    }
  }
}

return workflowData;

