formOptions = [];
for (const item of $input.all()) {
  dropDownValues = [];
  for (const credential of $('Merge').first().json.allCredentials) {
    if (credential.type == item.json.type) {
      dropDownValues.push({"option": credential.name});
    }
  }
  dropDownValues.push({"option": "[create new]"});
  formOptions.push({
      "fieldLabel": item.json.name,
      "fieldType": "dropdown",
      "requiredField": true,
      "fieldOptions": {
        "values": dropDownValues
      }
  });
}

return { "options": JSON.stringify(formOptions) };

