  let name = $('Map Credentials').first().json[credential.json.name];
  if (name == "[create new]") {
    data = {};
    if (type.includes("OAuth")) {
      data = { "clientId": "", "clientSecret": "" };
    }
    missingCredentials.push({
      "name": credential.json.name + " ⚠️",
      "type": type,
      "data": data
    });
  }
}

return missingCredentials;
