import uuid

crops = [item.json for item in _input.all()]
batch_size = int(_('Qdrant cluster variables').first()['json']['batchSize'])

def split_into_batches_add_uuids(array, batch_size):
    return [
      {
        "batch": array[i:i + batch_size],
        "uuids": [str(uuid.uuid4()) for j in range(len(array[i:i + batch_size]))]
      }
       for i in range(0, len(array), batch_size)
    ]

# Split crops into batches
batched_crops = split_into_batches_add_uuids(crops, batch_size)

return batched_crops
