  return (
post.media_type === "TEXT_POST" || 
post.media_type === "IMAGE" || 
post.media_type === "VIDEO" || 
post.media_type === "CAROUSEL_ALBUM" || 
post.media_type === "AUDIO");
});

// 抽取所需的欄位：id, permalink, timestamp
