// 確保 items 是否有內容
if (!items || items.length === 0) {
  console.log('No items found');
  return [];
}

// 取得輸入數據
const inputData = items[0].json;
console.log('Input data:', JSON.stringify(inputData, null, 2));

if (!inputData || !inputData.data || !Array.isArray(inputData.data)) {
  console.log('Invalid data structure');
  return [];
}

// 過濾出 username 為 yourThreadsName 的資料
const filteredPosts = inputData.data.filter(post => post.username === 'geekaz');
console.log('Filtered posts count:', filteredPosts.length);

// 處理每個 post，提取所需的資料
const processedData = filteredPosts.map(post => {
  // 初始化 mediaUrls，用來存放所有的 media_url
  let mediaUrls = [];

  // 如果有 children，則提取 children 裡的 media_url
  if (post.children?.data && Array.isArray(post.children.data)) {
    mediaUrls = post.children.data
      .map(child => child.media_url) // 提取每個 child 的 media_url
      .filter(url => url); // 過濾掉 undefined 或 null 的 URL
  } else if (post.media_url) {
    // 如果沒有 children，使用最外層的 media_url
    mediaUrls.push(post.media_url);
  }

  // 返回每個 post 的處理後結果
  return {
    text: post.text || '',
    media_urls: mediaUrls
  };
});

console.log('Processed data:', JSON.stringify(processedData, null, 2));

// 將結果轉換為 n8n 所需格式
return processedData.map(post => ({ json: post }));

