// 確保 items 是否有內容
if (!items || items.length === 0) {
  console.log('No items found');
  return [];
}

// 取得所有貼文
const posts = items.map(item => item.json).flat();
console.log('Number of posts:', posts.length);

// 取得第一篇貼文的基本資訊
const firstPost = posts[0] || {};

// 生成 blocks 結構
const blocks = [];

// 處理每個貼文
posts.forEach(post => {
  // 如果有文字，加入文字區塊
  if (post.text) {
    blocks.push({
      object: "block",
      type: "paragraph",
      paragraph: {
        rich_text: [{
          type: "text",
          text: { content: post.text }
        }]
      }
    });
  }

  // 如果有媒體連結，加入 embed 區塊
  if (post.media_urls && post.media_urls.length > 0) {
    post.media_urls.forEach(url => {
      blocks.push({
        object: "block",
        type: "embed",
        embed: { url }
      });
    });
  }
});

// 合併基本資訊和 blocks
const combinedPost = {
  id: firstPost.id || '',
  permalink: firstPost.permalink || '',
  username: firstPost.username || '',
  timestamp: firstPost.timestamp || '',
  blocks
};

// 將結果轉換為 n8n 所需格式
return [{ json: combinedPost }];
