const result = [];

for (const item of items) {
  const tags = item.json.tags;
  if (tags) {
    const latestTag = tags.includes('latest') ? 'latest' : null;
    const sortedTags = tags.filter(tag => tag !== 'latest')
                            .sort((a, b) => new Date(b.created) - new Date(a.created));
    const keepTags = sortedTags.slice(0, 10);
    if (latestTag) keepTags.push('latest');
    const deleteTags = sortedTags.slice(10);
    result.push(...deleteTags.map(tag => ({ json: { image: item.json.name, tag } })));
  }
}

return result;

const groupedData = items.reduce((acc, item) => {
  const name = item.json.name;
  if (!acc[name]) {
    acc[name] = [];
  }
  acc[name].push({
    tag: item.json.tag,
    created: item.json.created,
    digest: item.json.digest
  });
  return acc;
}, {});

return Object.keys(groupedData).map(name => ({
  json: { name, tags: groupedData[name] }
}));
