parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "company_name": {
      "type": "string",
      "description": "Official name of the company receiving funding"
    },
    "funding_round": {
      "type": "string",
      "description": "Type of funding round (Seed, Series A, B, C, etc.)",
      "enum": ["Pre-Seed", "Seed", "Series A", "Series B", "Series C", "Series D", "Series E+", "Growth Equity", "Late Stage", "Venture Round", "Convertible Note", "Debt Financing", "Grant", "Private Equity", "Other"]
    },
    "funding_amount": {
      "type": "object",
      "properties": {
        "value": {
          "type": "number",
          "description": "Numerical value of the funding amount"
        },
        "currency": {
          "type": "string",
          "description": "Currency of the funding amount",
          "default": "USD"
        }
      }
    },
    "announcement_date": {
      "type": "string",
      "format": "date",
      "description": "Date when the funding was publicly announced (YYYY-MM-DD)"
    },
    "lead_investor": {
      "type": ["string", "array"],
      "description": "Primary investor(s) leading the round. Can be single string or array for multiple leads."
    },
    "participating_investors": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Other firms or angels who participated in the round"
    },
    "industry": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Primary industry categories or verticals"
    },
    "company_description": {
      "type": "string",
      "description": "Brief explanation of what the company does"
    },
    "hq_location": {
      "type": "object",
      "properties": {
        "city": {
          "type": "string"
        },
        "country": {
          "type": "string"
        }
      },
      "description": "Company headquarters location"
    },
    "website_url": {
      "type": "string",
      "format": "uri",
      "description": "Company's official website"
    },
    "founding_year": {
      "type": "integer",
      "description": "Year the company was founded"
    },
    "founder_names": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Names of company founders"
    },
    "ceo_name": {
      "type": "string",
      "description": "Name of current CEO"
    },
    "employee_count": {
      "type": ["integer", "string"],
      "description": "Current number of employees (exact or range)"
    },
    "total_funding": {
      "type": "object",
      "properties": {
        "value": {
          "type": "number"
        },
        "currency": {
          "type": "string",
          "default": "USD"
        }
      },
      "description": "Total funding raised to date including this round"
    },
    "funding_purpose": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Stated use of funds (e.g., expansion, R&D, marketing)"
    },
    "business_model": {
      "type": "array",
      "items": {
        "type": "string",
        "enum": ["B2B", "B2C", "B2B2C", "D2C", "Marketplace", "SaaS", "Hardware", "Hybrid", "Other"]
      },
      "description": "Company's business model categories"
    },
    "valuation": {
      "type": "object",
      "properties": {
        "pre_money": {
          "type": "object",
          "properties": {
            "value": {
              "type": "number"
            },
            "currency": {
              "type": "string",
              "default": "USD"
            }
          }
        },
        "post_money": {
          "type": "object",
          "properties": {
            "value": {
              "type": "number"
            },
            "currency": {
              "type": "string",
              "default": "USD"
            }
          }
        }
      },
      "description": "Company valuation information (if disclosed)"
    },
    "previous_rounds": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "date": {
            "type": "string",
            "format": "date"
          },
          "round": {
            "type": "string"
          },
          "amount": {
            "type": "object",
            "properties": {
              "value": {
                "type": "number"
              },
              "currency": {
                "type": "string"
              }
            }
          },
          "investors": {
            "type": "array",
            "items": {
              "type": "string"
            }
          }
        }
      },
      "description": "Information about previous funding rounds"
    }
  }
}
