function capitalizeFirstLetter(val) {
  const str = String(val).trim();
  return str.charAt(0).toUpperCase() + str.slice(1);
}

// Get raw comma-separated string
const raw = $json.name;

// Step 1: Split, clean, and filter
const values = raw
  .split(',')
  .map(x => x.trim())
  .filter(x => x.length > 0 && !/^[A-Za-z0-9]{10,}$/.test(x)) // filter junk-like tokens
  .map(x => ({ option: capitalizeFirstLetter(x) }));

// Step 2: Add [create new]
values.push({ option: "[create new]" });

// Step 3: Build form config
const formOptions = {
  fieldLabel: "Dropdown Options",
  fieldType: "dropdown",
  requiredField: true,
  fieldOptions: {
    values
  }
};

// Step 4: Return as stringified JSON
return [
  {
    json: {
      fieldLabel: "Dropdown Options",
      fieldType: "dropdown",
      requiredField: true,
      fieldOptions: {
        values: values
      }
    }
  }
];
