let results = [];

for (const item of $input.all()) {
  for (key of Object.keys(item.binary)) {
        results.push({
            json: {},
            binary: {
                data: item.binary[key],
            }
        });
    }
}

return results;

const inputData = $input.all();

const outputData = inputData.map(item => {

  const outputValue = item.json.output || item.json.content;

  return {
    data: {
      output: outputValue 
    }
  };
});

return outputData;
