// Récupérer le texte à analyser depuis l'input
const texteExemple = $input.first().json.messages[0].text.body;

// Expression régulière modifiée pour capturer les URL avec ou sans protocole
const regex = /((?:https?:\/\/)?(?:www\.)?[a-zA-Z0-9.-]+\.[a-zA-Z]{2,6}(?:\/\S*)?)/g;

// Extraction des URL à partir du texte
const matches = texteExemple.match(regex) || [];

// Si des URL ont été trouvées, on les mappe en objets
if (matches.length > 0) {
    const output = matches.map(url => ({ url }));
    return output;
} else {
    // Si aucune URL n'est trouvée, on retourne les items d'entrée pour que le workflow continue
    return $input.all();
}

// Exemple : récupération du contenu HTML depuis le premier item
// Si vous avez un champ nommé "html" dans un noeud précédent.
const htmlContent = $input.first().json.html;

// Fonction de nettoyage du HTML
function nettoyerHTML(input) {
  if (typeof input !== 'string') {
    throw new Error("Expected HTML content as a string.");
  }
  let cleanedText = input;

  // 1. Retirer les liens <a> et leur contenu
  cleanedText = cleanedText.replace(/<a[^>]*>.*?<\/a>/gs, '');

  // 2. Retirer <script>, <style>, commentaires, etc.
  cleanedText = cleanedText.replace(/<script[^>]*>.*?<\/script>/gs, '');
  cleanedText = cleanedText.replace(/<style[^>]*>.*?<\/style>/gs, '');
  cleanedText = cleanedText.replace(/<!--[\s\S]*?-->/g, '');

  // 3. Insérer des retours à la ligne pour certaines balises
  cleanedText = cleanedText.replace(/<h[1-6][^>]*>/gi, '\n');
  cleanedText = cleanedText.replace(/<\/h[1-6]>/gi, '\n');
  cleanedText = cleanedText.replace(/<p[^>]*>/gi, '\n');
  cleanedText = cleanedText.replace(/<\/p>/gi, '\n');
  cleanedText = cleanedText.replace(/<ul[^>]*>/gi, '\n');
  cleanedText = cleanedText.replace(/<\/ul>/gi, '\n');
  cleanedText = cleanedText.replace(/<ol[^>]*>/gi, '\n');
  cleanedText = cleanedText.replace(/<\/ol>/gi, '\n');
  cleanedText = cleanedText.replace(/<li[^>]*>/gi, '- ');
  cleanedText = cleanedText.replace(/<\/li>/gi, '\n');

  // 4. Supprimer toutes les balises HTML restantes
  cleanedText = cleanedText.replace(/<[^>]+>/g, '');

  // 5. Supprimer (ou ajuster) les caractères spéciaux
  cleanedText = cleanedText.replace(/[^\w\sÀ-ÖØ-öø-ÿ]+/g, '');

  // 6. Normaliser les espaces multiples et trim
  cleanedText = cleanedText.replace(/\s+/g, ' ').trim();

  return cleanedText;
}

// Nettoyage
const result = nettoyerHTML($input.first().json.data);

// Retour d'un tableau contenant l'objet final
return [
  {
    json: {
      cleanedText: result
    }
  }
];
