// Loop over input items and modify them to match the response example, then return input.all()
for (const item of $input.all()) {
  item.json.params = {
    max_tokens: item.json.max_tokens,
    messages: [
      {
        content: item.json.query,
        role: "user"
      }
    ],
    model: item.json.model
  };
}

return $input.all();

const output = [];

for (const item of $input.all()) {
  const inputMessages = item.json.messages;
  const customId = item.json.custom_id;
  const model = item.json.model;
  const maxTokens = item.json.max_tokens;

  if (inputMessages && inputMessages.length > 0) {
    let systemMessageContent = undefined;
    const transformedMessages = [];

    // Process each message entry in sequence
    for (const messageObj of inputMessages) {
      // Extract system message if present
      if ('system' in messageObj) {
        systemMessageContent = messageObj.system;
      }

      // Process human and AI messages in the order they appear in the object keys
      // We need to determine what order the keys appear in the original object
      const keys = Object.keys(messageObj);

      for (const key of keys) {
        if (key === 'human') {
          transformedMessages.push({
            role: "user",
            content: messageObj.human
          });
        } else if (key === 'ai') {
          transformedMessages.push({
            role: "assistant",
            content: messageObj.ai
          });
        }
        // Skip 'system' as we already processed it
      }
    }

    const params = {
      model: model,
      max_tokens: maxTokens,
      messages: transformedMessages
    };

    if (systemMessageContent !== undefined) {
      params.system = systemMessageContent;
    }

    output.push({
      custom_id: customId,
      params: params
    });
  }
}

return output;
