let results = [],
  i = 0;

do {
  try {
    results = results.concat($items('Split events', 0, i));
  } catch (error) {
    return results;
  }
  i++;
} while(true);

const monthNames = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec' ];

let html = `<table style="width: 100%">`;
for (const item of $input.all()) {
  const eventDate = new Date(item.json.date[0]);

   html += `
    <tr>
      <td style="width: 60px; background-color: #2e2e32; font-family: sans-serif">
        <a href="https://www.songkick.com${item.json.eventLink}" style="color: #dcdfe6; text-decoration: none">
          <p style="font-weight: bold; text-align: center; margin: 5px 0 0; padding: 0 0.5em">${monthNames[eventDate.getMonth()]}</p>
          <p style="font-weight: bold; font-size: 1.5em; text-align: center; margin: 0 0 2px">${eventDate.getDate()}</p>
        </a>
      </td>
      <td style="background-color: #f2f4f8; font-family: sans-serif; padding: 0.3em 0.5em">
        <a href="https://www.songkick.com${item.json.eventLink}" style="color: #555555; text-decoration: none">
        <div>
          <p style="font-size: 1.2em; margin: 0"><b>${item.json.artist}</b>`

  if (item.json.support) {
    html = html + `<span style="color: #7d7d87; margin:0"> + ${item.json.support}</span>`;
  }

  html += `
          </p><p style="color: #7d7d87; margin: 0">${item.json.location.split(',')[0].replace(/(\r\n|\n|\r)/gm, "")}</p>
        </div>
        </a>
      </td>
  </tr>
   `
}
html += '</table>';

return { 
  "html": html,
  "total": $input.all().length 
};
