let globalData = $getWorkflowStaticData('global');
let state = "none"; // Default state

if (globalData && globalData.telegramStates) {
    let chatData = globalData.telegramStates[$json.message.chat.id.toString()];
    if (chatData) {
        if (chatData.waitingShipmentNumber === true) {
            state = "waitingShipmentNumber";
        } else if (chatData.waitingGPS === true) {
            state = "waitingGPS";
        } else if (chatData.waitingPhoto === true) {
            state = "waitingPhoto";
        }
    }
}
return { state };

