// Extract LinkedIn profile URLs from HTML
function extractLinkedInUrls(html) {
    // First decode any encoded HTML entities
    html = html.replace(/&amp;/g, '&')
               .replace(/\\u003d/g, '=')
               .replace(/\\x22/g, '"')
               .replace(/\\x26/g, '&')
               .replace(/\\x3e/g, '>')
               .replace(/\\x3c/g, '<');

    const patterns = [
        // Standard LinkedIn URLs in href
        /(?:https?:)?\/\/(?:[a-z]{2,}\.)?linkedin\.com\/in\/[a-zA-Z0-9._-]+(?:\/[a-z]{2})?/gi,
        // URLs in encoded strings
        /(?:"url"|url=)(?:[^"&]*?)(?:https?:)?\/\/(?:[a-z]{2,}\.)?linkedin\.com\/in\/[a-zA-Z0-9._-]+(?:\/[a-z]{2})?/gi,
        // URLs in JSON strings
        /"(?:https?:)?\/\/(?:[a-z]{2,}\.)?linkedin\.com\/in\/[a-zA-Z0-9._-]+(?:\/[a-z]{2})?"/gi
    ];

    const urls = new Set();

    patterns.forEach(pattern => {
        const matches = html.matchAll(pattern);
        for (const match of matches) {
            let url = match[0];

            // Clean up the URL
            url = url.replace(/^"url"|^url=|"$/g, '') // Remove url= prefix and quotes
                    .replace(/^["']|["']$/g, '')      // Remove surrounding quotes
                    .replace(/\\+/g, '')              // Remove backslashes
                    .trim();

            // Ensure URL has protocol
            if (!url.startsWith('http')) {
                url = 'https://' + url.replace(/^\/\//, '');
            }

            // Only include if it's a LinkedIn profile URL
            if (url.includes('linkedin.com/in/')) {
                // Clean the URL: remove tracking parameters and fragments
                url = url.split(/[?#&]/)[0];

                // Remove any trailing slashes
                url = url.replace(/\/$/, '');

                // Add to Set to remove duplicates
                urls.add(url);
            }
        }
    });

    return Array.from(urls);
}

// Get the HTML from input
const html = $input.first().json.data;

// Extract URLs and create array of objects
const linkedInProfiles = extractLinkedInUrls(html)
    .filter(url => !url.includes('google.com')) // Extra safety check to remove any Google URLs
    .map(url => ({
        linkedin_url: url
    }));

// Return the array of objects directly
return linkedInProfiles;

