var data = [];

for (item of items) {
  data.push({CityName: item.json.name+', '+item.json.sys.country,
             TempCur : item.json.main.temp,
             TempMin : item.json.main.temp_min,
             TempMax : item.json.main.temp_max
  });
}

return data;
