// Create the reply text
const replyText = "[reply post - hidden]";

// Calculate timestamp 1 second from now
const futureDate = new Date(Date.now() + 1000);

// Create the reply post object
const replyPostData = {
    repo: $('Set Bluesky Credentials').first().json.BlueskyHandle,
    collection: "app.bsky.feed.post",
    record: {
        "$type": "app.bsky.feed.post",
        text: replyText,
        reply: {
            root: {
                cid: $('Create Initial Post').first().json.cid,
                uri: $('Create Initial Post').first().json.uri
            },
            parent: {
                cid: $('Create Initial Post').first().json.cid,
                uri: $('Create Initial Post').first().json.uri
            }
        },
        createdAt: futureDate.toISOString()
    }
};

return replyPostData;

// Create the sibling text
const siblingText = "[first sibling - hidden]";

// Calculate timestamp 2 seconds from now
const futureDate = new Date(Date.now() + 2000);

// Create the sibling post object
const siblingPostData = {
    repo: $('Set Bluesky Credentials').first().json.BlueskyHandle,
    collection: "app.bsky.feed.post",
    record: {
        "$type": "app.bsky.feed.post",
        text: siblingText,
        reply: {
            root: {
                cid: $('Create Initial Post').first().json.cid,
                uri: $('Create Initial Post').first().json.uri
            },
            parent: {
                cid: $('Create Reply').first().json.cid,
                uri: $('Create Reply').first().json.uri
            }
        },
        createdAt: futureDate.toISOString()
    }
};

return siblingPostData;

// Create the initial post text
const postText = "[initial post - visible]";

// Create the parent post object
const postData = {
    repo: $('Set Bluesky Credentials').first().json.BlueskyHandle,
    collection: "app.bsky.feed.post",
    record: {
        $type: "app.bsky.feed.post",
        text: postText,
        createdAt: $now
    }
};

return postData;

