// Create the sibling text
const siblingText = `[${$json.name}]`;

// For the first iteration, use the parent IDs from Create Sibling node
// For subsequent iterations, use the Create Post node
const isFirstIteration = $runIndex === 0;
const cid = isFirstIteration 
    ? $('Create Sibling').first().json.cid 
    : $('Create Post').first().json.cid;
const uri = isFirstIteration 
    ? $('Create Sibling').first().json.uri 
    : $('Create Post').first().json.uri;

// Calculate timestamp 2 seconds from now
const futureDate = new Date(Date.now() + 2000);

// Create the sibling post object
const siblingPostData = {
    repo: $('Set Bluesky Credentials').first().json.BlueskyHandle,
    collection: "app.bsky.feed.post",
    record: {
        "$type": "app.bsky.feed.post",
        text: siblingText,
        reply: {
            root: {
                cid: $('Create Initial Post').first().json.cid,
                uri: $('Create Initial Post').first().json.uri
            },
            parent: {
                cid: cid,
                uri: uri
            }
        },
        createdAt: futureDate.toISOString()
    }
};

return siblingPostData;
