parameters.functionCode:
let deletion_nodes = [
  'Paddle Data Deletion',
  'Customer.io Data Deletion',
  'Zendesk Data Deletion'
]

const deletion_results = deletion_nodes.map(node_name => $items(node_name)[0].json);
const deletion_success = deletion_results.filter(json => json.success == true).length == deletion_nodes.length;

return [{
  json: {
    Result: deletion_success ? 'Done' : 'Error',
    Notes: deletion_results.map(json => json.service + ': ' + json.message).join('\n'),
    Processed: new Date().toISOString()
  }
}];

