const escapeHTML = str => str.replace(/[&<>'"]/g, 
  tag => ({
      '&': '&amp;',
      '<': '&lt;',
      '>': '&gt;',
      "'": '&#39;',
      '"': '&quot;'
    }[tag]));

let feedItems = [];
for (item of items) {
  feedItems.push(`<item>
  <title>${escapeHTML(item.json.data.name)}</title>
  <enclosure url="${item.json.data.associatedMedia.contentUrl}" length="${item.json.data.timeRequired * 20 * 1000}" type="${item.json.data.encodingFormat}"/>
  <guid isPermaLink="false">${item.json.data.identifier}</guid>
  <pubDate>${DateTime.fromISO(item.json.data.datePublished).toRFC2822()}</pubDate>
  <description>${escapeHTML(item.json.data.description)}</description>
</item>`);
}

return [{
  data: `<?xml version="1.0" encoding="UTF-8"?>
<rss version="2.0" xmlns:itunes="http://www.itunes.com/dtds/podcast-1.0.dtd" xmlns:content="http://purl.org/rss/1.0/modules/content/">
  <channel>
    <title>${escapeHTML(items[0].json.data.partOfSeries.name)}</title>
    <description>${escapeHTML(items[0].json.data.partOfSeries.about)}</description>
    <itunes:image href="${escapeHTML(items[0].json.data.image)}" />
    <language>${items[0].json.data.inLanguage}</language>
    <itunes:category text="Comedy" />
    <itunes:explicit>no</itunes:explicit>
    <link>${items[0].json.data.partOfSeries.url}</link>
    <copyright>Â© ${$now.toFormat('yyyy')} ${escapeHTML(items[0].json.data.productionCompany)}</copyright>
    <itunes:author>${escapeHTML(items[0].json.data.productionCompany)}</itunes:author>
    ${feedItems.join('\n')}
  </channel>
</rss>
`
}];
