parameters.query:
=query (
  $filter: IssueFilter
) {
  issues(
    filter: $filter
  ) {
    nodes {
      id
      identifier
      title
      description
      url
      createdAt
      updatedAt
      assignee {
        name
      }
      comments {
        nodes {
          id
          createdAt
          user {
            displayName
          }
          body
        }
      }
    }
  }
}

